mapper.insert:
INSERT INTO NOTICE(NOTICE_ID
			     , NOTICE_TITLE
			     , NOTICE_CONTENT
			     , NOTICE_IMG_NAME
			     , NOTICE_IMG
			     , CREATE_DATE)
           VALUES(SEQ_NNO.NEXTVAL
                , #{noticeTitle}
                , #{noticeContent}
                , #{noticeImgName}
                , #{noticeImg}
                , SYSDATE)

mapper.update:
UPDATE NOTICE
   SET STATUS = '0'
 WHERE NOTICE_ID = #{noticeNo}

UPDATE NOTICE
   SET STATUS = '1'
 WHERE NOTICE_ID = #{noticeNo}

UPDATE NOTICE
   SET NOTICE_TITLE = #{noticeTitle}
     , NOTICE_CONTENT = #{noticeContent}
     , NOTICE_IMG_NAME = #{noticeImgName}
     , NOTICE_IMG = #{noticeImg}
 WHERE NOTICE_ID = #{noticeNo}
